import React from 'react';
import { useState } from 'react';

const Deposit = () => {
  // State for points and transactions
  const [points, setPoints] = useState(0);
  const [transactions, setTransactions] = useState([]);

  // Function to handle depositing points
  const depositPoints = (amount) => {
    setPoints(points + amount);
    const transaction = {
      type: 'Deposit',
      amount: amount,
      date: new Date().toLocaleString()
    };
    setTransactions([...transactions, transaction]);
  };

  return (
    <div>
      <h1>Deposit of Bank Points</h1>
      <h2>Points Balance: {points}</h2>

      {/* Form for depositing points */}
      <form onSubmit={(e) => {
        e.preventDefault();
        const amount = parseInt(e.target.elements.amount.value);
        depositPoints(amount);
        e.target.reset();
      }}>
        <label htmlFor="amount">Enter Amount to Deposit:</label>
        <input type="number" id="amount" name="amount" required />
        <button type="submit">Deposit</button>
      </form>

      {/* List of transactions */}
      <h2>Transaction History</h2>
      <ul>
        {transactions.map((transaction, index) => (
          <li key={index}>
            <strong>{transaction.type}</strong>: {transaction.amount} Amounts ({transaction.date})
          </li>
        ))}
      </ul>
    </div>
  );
};

export default Deposit;
